import { useState, useEffect } from 'react';
import { jsPDF } from 'jspdf';
import './RequestItem.css';
import { fetchMaterialsByMaterialDescription, fetchUserById, fetchIssuer } from '../api';
import 'jspdf-autotable';

function RequestItem({ request }) {
  const [material, setMaterial] = useState(null);
  const [requesterName, setRequesterName] = useState('');
  const [issuerName, setIssuerName] = useState('');
  const [approver, setApprover] = useState('');

  // Fetch material data
  useEffect(() => {
    const fetchMaterialData = async () => {
      try {
        const response = await fetchMaterialsByMaterialDescription(request.description);
        setMaterial(response.data[0]);
      } catch (error) {
        console.error('Error fetching material data', error);
      }
    };
    fetchMaterialData();
  }, [request.description]);

  // Fetch requester name
  useEffect(() => {
    const fetchRequesterName = async () => {
      try {
        const response = await fetchUserById(request.requester);
        setRequesterName(response.data.username);
      } catch (error) {
        console.error('Error fetching requester name', error);
      }
    };
    if (request.requester) {
      fetchRequesterName();
    }
  }, [request.requester]);

  // Fetch issuer name if the request is issued
  useEffect(() => {
    const fetchIssuerName = async () => {
      try {
        const response = await fetchIssuer(request.issuedBy);
        setIssuerName(response.data.username);
      } catch (error) {
        console.error('Error fetching issuer name', error);
      }
    };
    if (request.status === 'Issued' && request.issuedBy) {
      fetchIssuerName();
    }
  }, [request.status, request.issuedBy]);

  // Fetch approver name if the request is approved
  useEffect(() => {
    const fetchApproverName = async () => {
      try {
        const response = await fetchIssuer(request.approvedBy);
        setApprover(response.data.username);
      } catch (error) {
        console.error('Error fetching approver name', error);
      }
    };
    if (request.status === 'Approved' && request.approvedBy) {
      fetchApproverName();
    }
  }, [request.status, request.approvedBy]);

  // Function to generate PDF in issuance slip format
  const downloadPDF = () => {
    const pdf = new jsPDF();
    const lineSpacing = 10;
    let yPosition = 20;

    // Header for Issuance Slip
    pdf.setFontSize(16);
    pdf.text('Issuance Slip', 105, yPosition, null, null, 'center');
    yPosition += lineSpacing * 2;

    // Request Details
    pdf.setFontSize(14);
    pdf.text('Request Details', 105, yPosition, null, null, 'center');
    yPosition += lineSpacing;

    pdf.autoTable({
      startY: yPosition,
      head: [['Field', 'Details']],
      body: [
        ['Description', request.description || 'N/A'],
        ['Requested By', requesterName || 'Unknown'],
        ['Quantity Requested', request.quantityRequested || 'N/A'],
        ['Status', request.status || 'N/A'],
        ['Relevant Manager', request.requestedManager || 'N/A'],
        ['Request Made On', new Date(request.createdAt).toLocaleString()],
        ...(request.status === 'Approved' ? [['Approved By', approver || 'Unknown']] : []),
        ...(request.status === 'Issued' ? [['Issued By', issuerName || 'Unknown']] : []),
      ],
      theme: 'grid',
      styles: { halign: 'center' },
    });

    yPosition = pdf.lastAutoTable.finalY + lineSpacing;

    // Material Data (if available)
    if (material) {
      pdf.setFontSize(14);
      pdf.text('Material Data', 105, yPosition, null, null, 'center');
      yPosition += lineSpacing;

      pdf.autoTable({
        startY: yPosition,
        head: [['Field', 'Details']],
        body: [
          ['Material', material.material || 'N/A'],
          ['Part No', material.part_no_store_code || 'N/A'],
          ['Available Quantity', material.quantity || 'N/A'],
          ['Machine', material.machine || 'N/A'],
          ['UOM', material.uom || 'N/A'],
        ],
        theme: 'grid',
        styles: { halign: 'center' },
      });

      yPosition = pdf.lastAutoTable.finalY + lineSpacing;
    }

    // Footer for Print Date/Time and Signature
    yPosition += lineSpacing;
    pdf.text('Printed On:', 20, yPosition);
    pdf.text(new Date().toLocaleString(), 80, yPosition);

    yPosition += lineSpacing * 2;
    pdf.text('Signature:', 20, yPosition);
    pdf.text('________________________', 20, yPosition + lineSpacing);

    // Save the PDF
    pdf.save(`issuance_slip_${request._id}.pdf`);
  };

  return (
    <li className="request-item">
      <h4>{request.description}</h4>
      <p>Requested By: {requesterName || 'Unknown'}</p>
      {request.status === 'Approved' && request.approvedBy && <p>Approved By: {approver || 'Unknown'}</p>}
      {request.status === 'Issued' && request.issuedBy && <p>Issued By: {issuerName || 'Unknown'}</p>}
      <p>Quantity Requested: {request.quantityRequested}</p>
      <p>Status: {request.status}</p>
      <p>Relevant Manager: {request.requestedManager}</p>
      <p>Request Made On: {new Date(request.createdAt).toLocaleString()}</p>

      <p>Material Data:</p>
      {material ? (
        <ul>
          <li>Material: {material.material}</li>
          <li>Part No: {material.part_no_store_code}</li>
          <li>Available Quantity: {material.quantity}</li>
          <li>Machine: {material.machine}</li>
          <li>UOM: {material.uom}</li>
        </ul>
      ) : (
        <p>No material data available</p>
      )}

      {/* Button to download the request content as a PDF */}
      <button onClick={downloadPDF}>Download as PDF</button>
    </li>
  );
}

export default RequestItem;
