import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import { getAllRequests } from '../api';
import RequestList from '../components/RequestList';
import RequestForm from '../components/RequestForm';
import ApprovalPanel from '../components/ApprovalPanel';
import IssuancePanel from '../components/IssuancePanel';
import './Dashboard.css';

function DashboardPage() {
  const [requests, setRequests] = useState([]);
  const [role, setRole] = useState('');
  const [previousRequestCount, setPreviousRequestCount] = useState(0); // To track request count for notifications
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    // Check if user is logged in
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // Redirect to login page if not logged in
      return;
    }

    // Get the role from local storage
    const userRole = localStorage.getItem('role');
    setRole(userRole);

    // Fetch requests from the API
    const fetchRequests = async () => {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const { data } = await getAllRequests(config);
        setRequests(data);

        // Compare request count to detect new requests
        if (previousRequestCount < data.length) {
          setNewRequest(true); // Set the flag for new requests
        } else {
          setNewRequest(false); // No new requests
        }

        setPreviousRequestCount(data.length); // Update previous request count
      } catch (error) {
        console.error('Error fetching requests', error);
      }
    };

    fetchRequests();
  }, [navigate, previousRequestCount]); // Add previousRequestCount to dependency array

  return (
    <div className="dashboard">
      {role === 'manager' && (
        <div className="dashboard-panels">
          <h2>Manager Dashboard</h2>
          {newRequest && <p className="notification">New requests or status updates!</p>} {/* Visual indicator */}
          <ApprovalPanel requests={requests} />
        </div>
      )}
      {role === 'fitter' && (
        <div className="dashboard-panels">
          <h2>Fitter Dashboard</h2>
          {newRequest && <p className="notification">New requests or status updates!</p>} {/* Visual indicator */}
          <RequestForm />
        </div>
      )}
      {role === 'issuer' && (
        <div className="dashboard-panels">
          <h2>Engineering Store Dashboard</h2>
          {newRequest && <p className="notification">New requests or status updates!</p>} {/* Visual indicator */}
          <IssuancePanel requests={requests} />
        </div>
      )}
      {!['manager', 'fitter', 'issuer'].includes(role) && (
        <p>Invalid role. Access denied.</p>
      )}
      <div className="request-list-container">
        <RequestList requests={requests} />
      </div>
    </div>
  );
}

export default DashboardPage;
