import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom'; // Import Link for navigation
import { login } from '../api';
import './Login.css';

function Login() {
  const [formData, setFormData] = useState({ username: '', password: '', role: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await login(formData);
      localStorage.setItem('token', data.token);
      localStorage.setItem('role', formData.role); // Store the role in local storage
      localStorage.setItem('username', formData.username); // Store the username in local storage
      navigate('/dashboard');
    } catch (error) {
      console.error('Login failed', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="login-form">
      <h2>Login</h2>
      <input type="text" name="username" placeholder="Username" onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
      <select name="role" onChange={handleChange} required>
        <option value="">Select Role</option>
        <option value="manager">Manager</option>
        <option value="fitter">Fitter</option>
        <option value="issuer">Issuer</option>
      </select>
      <button type="submit">Login</button>
      {/* Add link to registration page */}
      <p className="create-account">
        Don't have an account? <Link to="/register">Create a new account</Link>
      </p>
    </form>
  );
}

export default Login;
