import React, { useState, useEffect } from 'react';
import { fetchMaterials, fetchMaterialById, updateMaterial, deleteMaterial } from '../api';
import './Inventory.css';

function Inventory() {
  const [materials, setMaterials] = useState([]);
  const [selectedMaterial, setSelectedMaterial] = useState(null);
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getMaterials = async () => {
      try {
        const response = await fetchMaterials();
        setMaterials(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching materials:', err);
        setError('Error fetching materials');
        setLoading(false);
      }
    };

    getMaterials();
  }, []);

  const handleSelectMaterial = async (id) => {
    try {
      const response = await fetchMaterialById(id);
      setSelectedMaterial(response.data);
      setFormData(response.data);
    } catch (err) {
      console.error('Error fetching material by ID:', err);
      setError('Error fetching material');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleUpdate = async () => {
    try {
      await updateMaterial(selectedMaterial._id, formData);
      alert('Material updated successfully');
    } catch (err) {
      console.error('Error updating material:', err);
      setError('Error updating material');
    }
  };

  const handleDelete = async () => {
    try {
      await deleteMaterial(selectedMaterial._id);
      setMaterials((prevMaterials) => prevMaterials.filter((mat) => mat._id !== selectedMaterial._id));
      setSelectedMaterial(null);
      alert('Material deleted successfully');
    } catch (err) {
      console.error('Error deleting material:', err);
      setError('Error deleting material');
    }
  };

  if (loading) return <p>Loading materials...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="inventory-page">
      <h2>Inventory Management</h2>

      {/* Material selection */}
      <div className="material-selection">
        <label htmlFor="material-select">Select Material: </label>
        <select
          id="material-select"
          onChange={(e) => handleSelectMaterial(e.target.value)}
        >
          <option value="">Select a material</option>
          {materials.map((material) => (
            <option key={material._id} value={material._id}>
              {material.material_description}
            </option>
          ))}
        </select>
      </div>

      {/* Material form */}
      {selectedMaterial && (
        <div className="material-form">
          <h3>Edit Material</h3>
          <form>
            <label>
              SR #:
              <input
                type="number"
                name="sr_no"
                value={formData.sr_no || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Material:
              <input
                type="number"
                name="material"
                value={formData.material || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Part No / Store Code:
              <input
                type="text"
                name="part_no_store_code"
                value={formData.part_no_store_code || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Material Description:
              <input
                type="text"
                name="material_description"
                value={formData.material_description || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              UOM:
              <input
                type="text"
                name="uom"
                value={formData.uom || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Quantity:
              <input
                type="number"
                name="quantity"
                value={formData.quantity || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Unit Price:
              <input
                type="number"
                name="unit_price"
                value={formData.unit_price || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Total Price:
              <input
                type="number"
                name="total_price"
                value={formData.total_price || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Machine:
              <input
                type="text"
                name="machine"
                value={formData.machine || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Min:
              <input
                type="text"
                name="min"
                value={formData.min || ''}
                onChange={handleChange}
              />
            </label>
            <label>
              Max:
              <input
                type="text"
                name="max"
                value={formData.max || ''}
                onChange={handleChange}
              />
            </label>
          </form>
          <button onClick={handleUpdate}>Update</button>
          <button onClick={handleDelete}>Delete</button>
        </div>
      )}
    </div>
  );
}

export default Inventory;
