import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllRequests, fetchUserByUsername } from '../api'; // Import API functions
import RequestList from '../components/RequestList';
import RequestForm from '../components/RequestForm';
import ApprovalPanel from '../components/ApprovalPanel';
import IssuancePanel from '../components/IssuancePanel';
import './Dashboard.css';

function DashboardPage() {
  const [requests, setRequests] = useState([]);
  const [role, setRole] = useState('');
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Get the token from local storage to check if user is logged in
    const token = localStorage.getItem('token');
    if (!token) {
      // If token is missing, redirect to login page
      navigate('/login');
      return;
    }

    // Get the role and username from local storage
    const userRole = localStorage.getItem('role');
    const storedUsername = localStorage.getItem('username');
    setRole(userRole);
    setUsername(storedUsername);

    console.log('Retrieved role from localStorage:', userRole);
    console.log('Retrieved username from localStorage:', storedUsername);

    // Fetch the user's details from the database to confirm the role
    const verifyUserRole = async () => {
      try {
        // Fetch user by username from the database
        const { data: user } = await fetchUserByUsername(storedUsername);
        
        if (!user) {
          // If user is not found, redirect to login
          console.error('User not found');
          navigate('/login');
          return;
        }

        // Check if the user's role from the database matches the role in local storage
        if (user.role !== userRole) {
          console.error('Role mismatch. Redirecting to login.');
          navigate('/login'); // Redirect to login if roles don't match
          return;
        }

        console.log('Role verified:', user.role);

      } catch (error) {
        console.error('Error fetching user details', error);
        navigate('/login');
      }
    };

    verifyUserRole();

    // Fetch requests from the API
    const fetchRequests = async () => {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      try {
        const { data } = await getAllRequests(config);
        setRequests(data);
      } catch (error) {
        console.error('Error fetching requests', error);
      }
    };
    fetchRequests();
  }, [navigate]);

  return (
    <div className="dashboard">
      {role === 'manager' && (
        <div className="dashboard-panels">
          <h2>Manager Dashboard</h2>
          <ApprovalPanel requests={requests} />
        </div>
      )}
      {role === 'fitter' && (
        <div className="dashboard-panels">
          <h2>Fitter Dashboard</h2>
          <RequestForm />
        </div>
      )}
      {role === 'issuer' && (
        <div className="dashboard-panels">
          <h2>Engineering Store Dashboard</h2>
          <IssuancePanel requests={requests} />
        </div>
      )}
      {!['manager', 'fitter', 'issuer'].includes(role) && (
        <p>Invalid role. Access denied.</p>
      )}
      <div className="request-list-container">
        <RequestList requests={requests} />
      </div>
    </div>
  );
}

export default DashboardPage;
