import { useState, useEffect, useRef } from 'react';
import { jsPDF } from 'jspdf'; // Import jsPDF
import './RequestItem.css';
import { fetchMaterialsByMaterialDescription, fetchUserById, fetchIssuer } from '../api'; // Import the API functions

function RequestItem({ request }) {
  const [material, setMaterial] = useState(null);
  const [requesterName, setRequesterName] = useState(''); // State to hold requester name
  const [issuerName, setIssuerName] = useState(''); // State to hold the name of the person who issued the request
  const [approver, setApprover] = useState('');

  // Fetch material data
  useEffect(() => {
    const fetchMaterialData = async () => {
      try {
        const response = await fetchMaterialsByMaterialDescription(request.description);
        setMaterial(response.data[0]); // Assuming the description is unique and returns one material
      } catch (error) {
        console.error('Error fetching material data', error);
      }
    };
    fetchMaterialData();
  }, [request.description]);

  // Fetch requester name
  useEffect(() => {
    const fetchRequesterName = async () => {
      try {
        const response = await fetchUserById(request.requester); // Use the requester ID to fetch user details
        setRequesterName(response.data.username); // Assuming the response contains the username
      } catch (error) {
        console.error('Error fetching requester name', error);
      }
    };
    if (request.requester) {
      fetchRequesterName(); // Fetch the name only if requester ID is available
    }
  }, [request.requester]);

  // Fetch issuer name if request is issued
  useEffect(() => {
    const fetchIssuerName = async () => {
      try {
        const response = await fetchIssuer(request.issuedBy); // Use the issuedBy ID to fetch user details
        setIssuerName(response.data.username); // Assuming the response contains the username
      } catch (error) {
        console.error('Error fetching issuer name', error);
      }
    };
    if (request.status === 'Issued' && request.issuedBy) {
      fetchIssuerName();
    }
  }, [request.status, request.issuedBy]);

  // Fetch approver name if request is approved
  useEffect(() => {
    const fetchApproverName = async () => {
      try {
        const response = await fetchIssuer(request.approvedBy); // Use the approvedBy ID to fetch user details
        setApprover(response.data.username); // Assuming the response contains the username
      } catch (error) {
        console.error('Error fetching approver name', error);
      }
    };
    if (request.status === 'Approved' && request.approvedBy) {
      fetchApproverName();
    }
  }, [request.status, request.approvedBy]);

  // Function to generate PDF with text
// Function to generate PDF in issuance slip format
const downloadPDF = () => {
  const pdf = new jsPDF();
  const lineSpacing = 10;
  const headerYPosition = 20; // Position for the header
  let yPosition = headerYPosition + 10;

  // Header section for the Issuance Slip
  pdf.setFontSize(16);
  pdf.text('Issuance Slip', 105, headerYPosition, null, null, 'center'); // Title in the center

  // Request Details Section
  pdf.setFontSize(12);
  pdf.text('Request Details:', 10, yPosition);
  yPosition += lineSpacing;
  pdf.text(`Description: ${request.description}`, 10, yPosition);
  yPosition += lineSpacing;
  pdf.text(`Requested By: ${requesterName || 'Unknown'}`, 10, yPosition);
  yPosition += lineSpacing;
  pdf.text(`Quantity Requested: ${request.quantityRequested}`, 10, yPosition);
  yPosition += lineSpacing;
  pdf.text(`Status: ${request.status}`, 10, yPosition);
  yPosition += lineSpacing;
  pdf.text(`Request Made On: ${new Date(request.createdAt).toLocaleString()}`, 10, yPosition);
  yPosition += lineSpacing;

  // Approver and Issuer Section (if applicable)
  if (request.status === 'Approved' && approver) {
    yPosition += lineSpacing;
    pdf.text('Approval Details:', 10, yPosition);
    yPosition += lineSpacing;
    pdf.text(`Approved By: ${approver || 'Unknown'}`, 10, yPosition);
  }

  if (request.status === 'Issued' && issuerName) {
    yPosition += lineSpacing;
    pdf.text('Issuer Details:', 10, yPosition);
    yPosition += lineSpacing;
    pdf.text(`Issued By: ${issuerName || 'Unknown'}`, 10, yPosition);
  }

  // Material Data Section
  if (material) {
    yPosition += lineSpacing * 2;
    pdf.text('Material Data:', 10, yPosition);
    yPosition += lineSpacing;
    pdf.text(`Material: ${material.material}`, 10, yPosition);
    yPosition += lineSpacing;
    pdf.text(`Part No: ${material.part_no_store_code}`, 10, yPosition);
    yPosition += lineSpacing;
    pdf.text(`Available Quantity: ${material.quantity}`, 10, yPosition);
    yPosition += lineSpacing;
    pdf.text(`Machine: ${material.machine}`, 10, yPosition);
    yPosition += lineSpacing;
    pdf.text(`UOM: ${material.uom}`, 10, yPosition);
  }

  // Footer section for the Issuance Slip (Optional)
  yPosition += lineSpacing * 2;
  pdf.text('Signature:', 10, yPosition);
  pdf.text('________________________', 10, yPosition + lineSpacing);
  pdf.text('Date:', 150, yPosition);
  pdf.text('________________________', 150, yPosition + lineSpacing);

  // Save the PDF with a dynamic name
  pdf.save(`issuance_slip_${request._id}.pdf`);
};


  return (
    <li className="request-item">
      <h4>{request.description}</h4>
      <p>Requested By: {requesterName || 'Unknown'}</p>
      {request.status === 'Approved' && request.approvedBy && (
        <p>Approved By: {approver || 'Unknown'}</p>
      )}
      {request.status === 'Issued' && request.issuedBy && (
        <p>Issued By: {issuerName || 'Unknown'}</p>
      )}
      <p>Quantity Requested: {request.quantityRequested}</p>
      <p>Status: {request.status}</p>
      <p>Relevant Manager: {request.requestedManager}</p>
      <p>Request Made On: {new Date(request.createdAt).toLocaleString()}</p>

      <p>Material Data:</p>
      {material && (
        <ul>
          <li>Material: {material.material}</li>
          <li>Part No: {material.part_no_store_code}</li>
          <li>Available Quantity: {material.quantity}</li>
          <li>Machine: {material.machine}</li>
          <li>UOM: {material.uom}</li>
        </ul>
      )}

      {/* Button to download the request content as a PDF */}
      <button onClick={downloadPDF}>Download as PDF</button>
    </li>
  );
}

export default RequestItem;
